import React from 'react';
import './Sidebar.css';
import explogo from './img/explogo.png';

    const Sidebar = () => {
      return (
        <div className="sidebar">
          <div className="logo"><img src={explogo} alt="explogo" className="logo-img"/></div>
          <h2>ASSET <br/>MANAGEMENT</h2>
            <h3>Menu</h3>
          <ul>
            <li className={location.pathname === '/dashboard' ? 'active' : ''}>
          <Link to="/dashboard">Home</Link>
          </li>
            <li className={location.pathname === '/assets' ? 'active' : ''}>
          <Link to="/assets">Assets</Link>
          </li>
            <li className={location.pathname === '/custodians' ? 'active' : ''}>
          <Link to="/custodians">Custodians</Link>
          </li>
            <li><a href="/settings">Settings</a></li>
            <li className={location.pathname === '/settings' ? 'active' : ''}>
          <Link to="/settings">Settings</Link>
          </li>

          </ul>
        </div>
      );
    };

    export default Sidebar;
