import React, { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUserCircle } from '@fortawesome/free-solid-svg-icons';
import { useNavigate } from 'react-router-dom';
import './headerdashboard.css';  // Ensure you have appropriate styles for dropdown

const header = ({ userData }) => {
  const [isDropdownVisible, setDropdownVisible] = useState(false);
  const navigate = useNavigate(); 

  const handleLogout = (e) => {
    e.preventDefault();
    console.log('Logging out');
    document.cookie = 'user_data=; Max-Age=0; path=/'; // Clear the cookie
    sessionStorage.clear();
    navigate('/login');
  };

  const toggleDropdown = () => {
    setDropdownVisible(!isDropdownVisible);
  };

  return (
    <div className="Header">
      <div className="user-icon" onClick={toggleDropdown}>
        <FontAwesomeIcon 
          icon={faUserCircle} 
          className="topnav-right" 
        />
        {userData && (
          <div className={`dropdown-menu ${isDropdownVisible ? 'show' : ''}`}>
            <span className="dropdown-email">{userData.email_address}</span>
            <button className="dropdown-logout" onClick={handleLogout}>
              Logout
            </button>
          </div>
        )}
      </div>
      <h2 className="dashboard-h2">Overview</h2>
    </div>
  );
};

export default header;
