import React, {useState} from 'react';
import { Form, Input, Select, DatePicker, InputNumber, Radio, Upload, Button, Space, Avatar } from 'antd';
import { UploadOutlined, UserOutlined  } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import '../dashboard.css';

const Settings = () => {
const navigate = useNavigate(); 

const { Option } = Select;
const [profilePicture, setProfilePicture] = useState(null);


  const onFinish = (values) => {
    console.log('Received values:', values);
  };

  const handleUpload = (info) => {
    const reader = new FileReader();
    reader.onload = e => {
      setProfilePicture(e.target.result);
    };
    reader.readAsDataURL(info.file.originFileObj);
  };

  const handleLogout = (e) => {
    e.preventDefault();

    console.log('Logging out');
    navigate('/login');
  };

  return (

    <div className="dashboard_wrapper">
    <Sidebar/>

        <div className="Header">
          <button className="topnav-right" onClick={handleLogout}>Out</button>
              <h2 className="dashboard-h2">Settings</h2>
        </div> 

          <div className="dashboardpage">


            <Form.Item className="Setprofile">
              <br/>
            <Space direction="vertical" align="center">
              <Avatar
                size={100}
                src={profilePicture}
                icon={!profilePicture && <UserOutlined />}
                style={{ borderRadius: '50%', marginBottom: 16 }}

              />
              <Upload 
                name="profile"
                showUploadList={false}
                beforeUpload={() => false}
                onChange={handleUpload}
              >
                <Button type="default" shape="triangle"icon={<UploadOutlined />}> Change Profile  </Button>
              </Upload>
            </Space>
          </Form.Item>
          <div className="grid-container">

    <Form className="Setform" layout="vertical" onFinish={onFinish} initialValues={{
            language: 'en',
            country: 'Ghana',
            timezone: 'GMT',
            textSize: 14,
            theme: 'light',
            dashboardFields: 10,
          }}
        >

          <Form.Item label="Language" name="language">
            <Select className="Selectbox">
              <Option value="en">English</Option>
              <Option value="es">Spanish</Option>
              <Option value="fr">French</Option>
              <Option value="zh">Chinese</Option>
            </Select>
          </Form.Item>

          <Form.Item label="Country" name="country">
            <Select className="Selectbox">
              <Option value="us">United States</Option>
              <Option value="uk">United Kingdom</Option>
              <Option value="ca">Canada</Option>
              <Option value="gh">Ghana</Option>
              <Option value="fr">France</Option>
              <Option value="cn">China</Option>
              <Option value="de">Germany</Option>
              <Option value="it">Italy</Option>
              <Option value="ng">Nigeria</Option>
              <Option value="tg">Togo</Option>

            </Select>
          </Form.Item>

          <Form.Item label="Timezone" name="timezone">
            <Select className="Selectbox">
              <Option value="GMT">GMT</Option>
              <Option value="CST">CST</Option>
              <Option value="EST">EST</Option>
            </Select>
          </Form.Item>

          <Form.Item label="Date" name="date" >
            <DatePicker />
         </Form.Item>


        <div className="Setleft">

      <Form.Item label="Text Size" name="textSize">
        <InputNumber min={10} max={30} />
      </Form.Item>


      <div>
          <Form.Item label="Theme" name="theme">
            <Radio.Group>
              <Radio value="light">Light</Radio>
              <Radio value="dark">Dark</Radio>
            </Radio.Group>
          </Form.Item>
          </div>

          <div>
          <Form.Item label="Number of Fields to Display on Dashboard" name="dashboardFields">
            <InputNumber min={5} max={20} />
          </Form.Item>
          </div>

          </div>

     <div className="setbutton">
      <Form.Item>
        <Space>
          <Button type="primary" htmlType="submit">Save</Button>
          <Button htmlType="reset">Reset</Button>
        </Space>
      </Form.Item>
      </div>
   </Form>
        </div>
      </div>
     </div>
     );
    };  



export default Settings;
