import React, { useState } from "react";
import { useHistory } from 'react-router-dom';
import { MdOutlineAlternateEmail } from "react-icons/md";
import { RiLockPasswordLine } from "react-icons/ri";
 import explogo from './img/explogo.png';

 const Login = (props) => {

    const [email, setEmail] = useState('');
    const [Password, setPassword] = useState('');

    const handlesubmit = (e) => {
        e.preventDefault();
        console.log();

        if (!email || !Password || !ecard_id) {
            setError('Please fill in all fields');
            return;
          }

          if (!/\S+@\S+\.\S+/.test(email)) {
            setError('Email address is invalid');
            return;
          }

          setError('');

          console.log('Logging in with:', { email, password });

          history.push('/dashboard');
    }

    return (
        <div className="Form-container">

            <div className="Image"><img src={explogo} alt="explogo" /></div>

            <h2>ASSET MANAGEMENT</h2>
            <p>Welcome Back!</p>
            <form className="Loginform" onSubmit={(handlesubmit)}>

                <div className="input_parent">
                <input value ={email} onChange={(e) => setEmail(e.target.value)} type="email" placeholder="email" id="email" name="email"/> 
                <div className="icon_parent"><MdOutlineAlternateEmail classname="icon" color="grey"/></div>
                </div>

                <div className="input_parent">
                    <input value ={Password} onChange={(e) =>setPassword(e.target.value)} type="password" placeholder="password" id="password" name="password"/>
                    <div className="icon_parent"><RiLockPasswordLine classname="icon" color="grey"/></div>
                </div>


                <button type="submit">Login</button>

            <button className="link-btn" onClick={() => props.onFormSwitch('Register')}> Don't have an account? Register here.</button>
            </form>
        </div>
    )
}
export default Login;
