import React from 'react';
import { useNavigate } from 'react-router-dom';
import {MenuUnfoldOutlined, PlusOutlined, BarsOutlined} from "@ant-design/icons";
import {Card, Space} from 'antd';
import Sidebar from './Sidebar';
import { Table } from 'antd';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch } from '@fortawesome/free-solid-svg-icons';
import '../dashboard.css';
import {data} from '../JSON data/assetsview';
import { Button, Flex, Tooltip } from 'antd';
import Assetsformfilter from '../Filters/assetsformfilter';



const Assets = () => {
const navigate = useNavigate(); 


  const handleLogout = (e) => {
    e.preventDefault();

    console.log('Logging out');
    navigate('/login');
  };

  const columns = [
    {
    title: 'ID',
    dataIndex: 'id',
    key: 'id',
},

{
    title: 'NAME',
    dataIndex: 'name',
    key: 'name',
},
{
    title: 'ASSET_TYPE',
    dataIndex: 'asset_type',
    key: 'asset_type',
},
{
    title: 'SERIAL_NO',
    dataIndex: 'serial_no',
    key: 'serial_no',
},
{
    title: 'OS',
    dataIndex: 'os',
    key: 'os',
},
{
    title: 'MODEL',
    dataIndex: 'model',
    key: 'model',
},
{
    title: 'VENDOR',
    dataIndex: 'vendor',
    key: 'vendor',
},
{
    title: 'SOFTWARE',
    dataIndex: 'software',
    key: 'software',
},
{
    title: 'DATE',
    dataIndex: 'date',
    key: 'date',
},
];

  return (

    <div className="dashboard_wrapper">
    <Sidebar/>

          <div className="Header">
          <button className="topnav-right" onClick={handleLogout}>Out</button>
              <h2 className="dashboard-h2">Assets</h2>
              </div>   

          <div className="dashboardpage">

            {/* <div className="Filters"> */}
          <div className="dashboardpage">
            <div className="sidebar-footer">
              <div className="search-bar">
              <input type="text" placeholder="Search..." />
              <button>
              <FontAwesomeIcon icon={faSearch} />
              </button>
               </div>
            </div>

                <Space  >
                  <Card className="Filters">
                    <Space>
                      <MenuUnfoldOutlined className= "icon asset-icon"/>
                    </Space>
                  </Card>

                  <Card className="Filters">
                    <Space>
                      <BarsOutlined className= "icon asset-icon"/>
                    </Space>
                  </Card>

                  <Card className="Filters">
                    <Space>
                    <Flex gap="large" vertical>
                      <Flex wrap gap="large">
                      <Tooltip title="Add Asset">
                      <Button type="text" shape="triangle" icon={<PlusOutlined className= "icon asset-icon"/>} />
                      </Tooltip>
                      </Flex>
                    </Flex>

                    </Space>
                  </Card>

                </Space>

            <Assetsformfilter />

            <Table className="assetbox"
                      dataSource={data}
                      columns={columns}
                      rowKey="id"
                      pagination={{ pageSize: 7 }}
                  />

          </div> 
     </div>
     </div>


  );
};


export default Assets;
